@import "tailwindcss";
@import "tw-animate-css";

@custom-variant dark (&:is(.dark *));

@theme inline {
  --color-background: var(--background);
  --color-foreground: var(--foreground);
  --font-sans: var(--font-geist-sans);
  --font-mono: var(--font-geist-mono);
  --color-sidebar-ring: var(--sidebar-ring);
  --color-sidebar-border: var(--sidebar-border);
  --color-sidebar-accent-foreground: var(--sidebar-accent-foreground);
  --color-sidebar-accent: var(--sidebar-accent);
  --color-sidebar-primary-foreground: var(--sidebar-primary-foreground);
  --color-sidebar-primary: var(--sidebar-primary);
  --color-sidebar-foreground: var(--sidebar-foreground);
  --color-sidebar: var(--sidebar);
  --color-chart-5: var(--chart-5);
  --color-chart-4: var(--chart-4);
  --color-chart-3: var(--chart-3);
  --color-chart-2: var(--chart-2);
  --color-chart-1: var(--chart-1);
  --color-ring: var(--ring);
  --color-input: var(--input);
  --color-border: var(--border);
  --color-destructive: var(--destructive);
  --color-accent-foreground: var(--accent-foreground);
  --color-accent: var(--accent);
  --color-muted-foreground: var(--muted-foreground);
  --color-muted: var(--muted);
  --color-secondary-foreground: var(--secondary-foreground);
  --color-secondary: var(--secondary);
  --color-primary-foreground: var(--primary-foreground);
  --color-primary: var(--primary);
  --color-popover-foreground: var(--popover-foreground);
  --color-popover: var(--popover);
  --color-card-foreground: var(--card-foreground);
  --color-card: var(--card);
  --radius-sm: calc(var(--radius) - 4px);
  --radius-md: calc(var(--radius) - 2px);
  --radius-lg: var(--radius);
  --radius-xl: calc(var(--radius) + 4px);
}

:root {
  --radius: 0.625rem;
  --background: oklch(1 0 0);
  --foreground: oklch(0.145 0 0);
  --card: oklch(1 0 0);
  --card-foreground: oklch(0.145 0 0);
  --popover: oklch(1 0 0);
  --popover-foreground: oklch(0.145 0 0);
  --primary: oklch(0.205 0 0);
  --primary-foreground: oklch(0.985 0 0);
  --secondary: oklch(0.97 0 0);
  --secondary-foreground: oklch(0.205 0 0);
  --muted: oklch(0.97 0 0);
  --muted-foreground: oklch(0.556 0 0);
  --accent: oklch(0.97 0 0);
  --accent-foreground: oklch(0.205 0 0);
  --destructive: oklch(0.577 0.245 27.325);
  --border: oklch(0.922 0 0);
  --input: oklch(0.922 0 0);
  --ring: oklch(0.708 0 0);
  --chart-1: oklch(0.646 0.222 41.116);
  --chart-2: oklch(0.6 0.118 184.704);
  --chart-3: oklch(0.398 0.07 227.392);
  --chart-4: oklch(0.828 0.189 84.429);
  --chart-5: oklch(0.769 0.188 70.08);
  --sidebar: oklch(0.985 0 0);
  --sidebar-foreground: oklch(0.145 0 0);
  --sidebar-primary: oklch(0.205 0 0);
  --sidebar-primary-foreground: oklch(0.985 0 0);
  --sidebar-accent: oklch(0.97 0 0);
  --sidebar-accent-foreground: oklch(0.205 0 0);
  --sidebar-border: oklch(0.922 0 0);
  --sidebar-ring: oklch(0.708 0 0);
}

.dark {
  --background: oklch(0.145 0 0);
  --foreground: oklch(0.985 0 0);
  --card: oklch(0.205 0 0);
  --card-foreground: oklch(0.985 0 0);
  --popover: oklch(0.205 0 0);
  --popover-foreground: oklch(0.985 0 0);
  --primary: oklch(0.922 0 0);
  --primary-foreground: oklch(0.205 0 0);
  --secondary: oklch(0.269 0 0);
  --secondary-foreground: oklch(0.985 0 0);
  --muted: oklch(0.269 0 0);
  --muted-foreground: oklch(0.708 0 0);
  --accent: oklch(0.269 0 0);
  --accent-foreground: oklch(0.985 0 0);
  --destructive: oklch(0.704 0.191 22.216);
  --border: oklch(1 0 0 / 10%);
  --input: oklch(1 0 0 / 15%);
  --ring: oklch(0.556 0 0);
  --chart-1: oklch(0.488 0.243 264.376);
  --chart-2: oklch(0.696 0.17 162.48);
  --chart-3: oklch(0.769 0.188 70.08);
  --chart-4: oklch(0.627 0.265 303.9);
  --chart-5: oklch(0.645 0.246 16.439);
  --sidebar: oklch(0.205 0 0);
  --sidebar-foreground: oklch(0.985 0 0);
  --sidebar-primary: oklch(0.488 0.243 264.376);
  --sidebar-primary-foreground: oklch(0.985 0 0);
  --sidebar-accent: oklch(0.269 0 0);
  --sidebar-accent-foreground: oklch(0.985 0 0);
  --sidebar-border: oklch(1 0 0 / 10%);
  --sidebar-ring: oklch(0.556 0 0);
}

@layer base {
  * {
    @apply border-border outline-ring/50;
  }
  body {
    @apply bg-background text-foreground;
  }
}

/* Salesforce Lightning Design System Colors */

:root {
  /* Primary Colors - Salesforce Blue */
  --slds-blue-50: #e3f2fd;
  --slds-blue-100: #bbdefb;
  --slds-blue-200: #90caf9;
  --slds-blue-300: #64b5f6;
  --slds-blue-400: #42a5f5;
  --slds-blue-500: #2196f3;
  --slds-blue-600: #1976d2;
  --slds-blue-700: #1565c0;
  --slds-blue-800: #0d47a1;
  --slds-blue-900: #0a3d91;

  /* Salesforce Green */
  --slds-green-50: #e8f5e8;
  --slds-green-100: #c8e6c9;
  --slds-green-200: #a5d6a7;
  --slds-green-300: #81c784;
  --slds-green-400: #66bb6a;
  --slds-green-500: #4caf50;
  --slds-green-600: #388e3c;
  --slds-green-700: #2e7d32;
  --slds-green-800: #1b5e20;
  --slds-green-900: #0d4f14;

  /* Neutral Colors - Grays */
  --slds-gray-50: #fafafa;
  --slds-gray-100: #f5f5f5;
  --slds-gray-200: #eeeeee;
  --slds-gray-300: #e0e0e0;
  --slds-gray-400: #bdbdbd;
  --slds-gray-500: #9e9e9e;
  --slds-gray-600: #757575;
  --slds-gray-700: #616161;
  --slds-gray-800: #424242;
  --slds-gray-900: #212121;

  /* Semantic Colors */
  --slds-success: #04844b;
  --slds-success-light: #e8f5e8;
  --slds-success-dark: #2e7d32;

  --slds-warning: #fe9339;
  --slds-warning-light: #fff3e0;
  --slds-warning-dark: #f57c00;

  --slds-error: #ea001e;
  --slds-error-light: #ffebee;
  --slds-error-dark: #c62828;

  --slds-info: #0176d3;
  --slds-info-light: #e3f2fd;
  --slds-info-dark: #1565c0;

  /* Additional Salesforce Colors */
  --slds-purple: #9c27b0;
  --slds-purple-light: #f3e5f5;
  --slds-purple-dark: #7b1fa2;

  --slds-orange: #ff9800;
  --slds-orange-light: #fff3e0;
  --slds-orange-dark: #f57c00;

  --slds-red: #f44336;
  --slds-red-light: #ffebee;
  --slds-red-dark: #d32f2f;

  --slds-pink: #e91e63;
  --slds-pink-light: #fce4ec;
  --slds-pink-dark: #c2185b;

  --slds-indigo: #3f51b5;
  --slds-indigo-light: #e8eaf6;
  --slds-indigo-dark: #303f9f;

  --slds-teal: #009688;
  --slds-teal-light: #e0f2f1;
  --slds-teal-dark: #00695c;

  /* Salesforce Brand Colors */
  --slds-brand-primary: #0176d3;
  --slds-brand-secondary: #014486;
  --slds-brand-tertiary: #032d60;

  /* Background Colors */
  --slds-bg-primary: #ffffff;
  --slds-bg-secondary: #f3f2f2;
  --slds-bg-tertiary: #eef1f6;

  /* Text Colors */
  --slds-text-primary: #181818;
  --slds-text-secondary: #514f4d;
  --slds-text-tertiary: #706e6b;
  --slds-text-inverse: #ffffff;

  /* Border Colors */
  --slds-border-primary: #dddbda;
  --slds-border-secondary: #c9c7c5;
  --slds-border-tertiary: #b0adab;

  /* Shadow Colors */
  --slds-shadow-1: rgba(0, 0, 0, 0.1);
  --slds-shadow-2: rgba(0, 0, 0, 0.15);
  --slds-shadow-3: rgba(0, 0, 0, 0.2);
}

/* Salesforce Button Variants */
.slds-button--brand {
  background-color: var(--slds-brand-primary);
  border-color: var(--slds-brand-primary);
  color: var(--slds-text-inverse);
}

.slds-button--brand:hover {
  background-color: var(--slds-brand-secondary);
  border-color: var(--slds-brand-secondary);
}

.slds-button--destructive {
  background-color: var(--slds-error);
  border-color: var(--slds-error);
  color: var(--slds-text-inverse);
}

.slds-button--destructive:hover {
  background-color: var(--slds-error-dark);
  border-color: var(--slds-error-dark);
}

.slds-button--success {
  background-color: var(--slds-success);
  border-color: var(--slds-success);
  color: var(--slds-text-inverse);
}

.slds-button--success:hover {
  background-color: var(--slds-success-dark);
  border-color: var(--slds-success-dark);
}

/* Salesforce Card Styles */
.slds-card {
  background-color: var(--slds-bg-primary);
  border: 1px solid var(--slds-border-primary);
  border-radius: 0.25rem;
  box-shadow: 0 2px 2px 0 var(--slds-shadow-1);
}

/* Salesforce Form Styles */
.slds-input {
  border: 1px solid var(--slds-border-primary);
  border-radius: 0.25rem;
  color: var(--slds-text-primary);
}

.slds-input:focus {
  border-color: var(--slds-brand-primary);
  box-shadow: 0 0 3px var(--slds-brand-primary);
}

/* Salesforce Alert Styles */
.slds-alert--success {
  background-color: var(--slds-success-light);
  border-color: var(--slds-success);
  color: var(--slds-success-dark);
}

.slds-alert--warning {
  background-color: var(--slds-warning-light);
  border-color: var(--slds-warning);
  color: var(--slds-warning-dark);
}

.slds-alert--error {
  background-color: var(--slds-error-light);
  border-color: var(--slds-error);
  color: var(--slds-error-dark);
}

.slds-alert--info {
  background-color: var(--slds-info-light);
  border-color: var(--slds-info);
  color: var(--slds-info-dark);
}

import * as React from "react"
import { Slot } from "@radix-ui/react-slot"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const buttonVariants = cva(
  "inline-flex items-center justify-center gap-2 whitespace-nowrap rounded-md text-sm font-medium transition-all disabled:pointer-events-none disabled:opacity-50 [&_svg]:pointer-events-none [&_svg:not([class*='size-'])]:size-4 shrink-0 [&_svg]:shrink-0 outline-none focus-visible:border-ring focus-visible:ring-ring/50 focus-visible:ring-[3px] aria-invalid:ring-destructive/20 dark:aria-invalid:ring-destructive/40 aria-invalid:border-destructive",
  {
    variants: {
      variant: {
        default:
          "bg-primary text-primary-foreground shadow-xs hover:bg-primary/90",
        destructive:
          "bg-destructive text-white shadow-xs hover:bg-destructive/90 focus-visible:ring-destructive/20 dark:focus-visible:ring-destructive/40 dark:bg-destructive/60",
        outline:
          "border bg-background shadow-xs hover:bg-accent hover:text-accent-foreground dark:bg-input/30 dark:border-input dark:hover:bg-input/50",
        secondary:
          "bg-secondary text-secondary-foreground shadow-xs hover:bg-secondary/80",
        ghost:
          "hover:bg-accent hover:text-accent-foreground dark:hover:bg-accent/50",
        link: "text-primary underline-offset-4 hover:underline",
      },
      size: {
        default: "h-9 px-4 py-2 has-[>svg]:px-3",
        sm: "h-8 rounded-md gap-1.5 px-3 has-[>svg]:px-2.5",
        lg: "h-10 rounded-md px-6 has-[>svg]:px-4",
        icon: "size-9",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
    },
  }
)

function Button({
  className,
  variant,
  size,
  asChild = false,
  ...props
}: React.ComponentProps<"button"> &
  VariantProps<typeof buttonVariants> & {
    asChild?: boolean
  }) {
  const Comp = asChild ? Slot : "button"

  return (
    <Comp
      data-slot="button"
      className={cn(buttonVariants({ variant, size, className }))}
      {...props}
    />
  )
}

export { Button, buttonVariants }

